import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Update.css'
import { useParams } from 'react-router-dom';


function Update() {

    let { id } = useParams();

    // State variable to hold form data
    const [product, setProduct] = useState({
        name: '',
        price: '',
        quality: '',
        brand: '',
        color: '',
        size: '',
        description: '',
        category: '',
        quantity: '',
        weight: '',
    });

    // Function to fetch the product data on component mount
    useEffect(() => {
        const getItem = async (itemId) => {
            try {
                const response = await fetch(`http://localhost:4000/product/${itemId}`, {
                    method: 'GET',
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const data = await response.json();
                setProduct(data); // Set the fetched product data into state
            } catch (error) {
                console.error('Error fetching item:', error);
            }
        };

        getItem(id); // Call getItem with the id from useParams
    }, [id]);

    // Update individual form field values in state
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProduct({ ...product, [name]: value });
    };

    // Handle form submission
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        
        try {
            const response = await fetch(`http://localhost:4000/product/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(product),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Assuming successful update, you might want to handle success here
            console.log('Product updated successfully');
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };

    return (
        <div className="form scroll-y scroll-hidden-x w-full">
            <form action="" className="w-full flex flex-column gap-4 p-10">
                <div className="add-image-container w-full flex j-center">
                    <div className="add-image r-10 p-1 relative">
                        <div className="add-image-text absolute inset flex flex-col a-center j-center">
                            <span className="material-symbols-rounded for">
                                add
                            </span>
                            <p>
                                Add image
                            </p>
                        </div>
                        <input type="file" name='image' className='file-input size-full' />
                    </div>
                </div>
                <div className="details grid gap">
                    <div className="items flex flex-column gap">
                        Product name
                        <input value={product.name} type="text" name='name' placeholder="Product name" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Price
                        <input value={product.price} type="text" name='price' placeholder="Price" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Quality
                        <input value={product.quality} type="text" name='quality' placeholder="Quality" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Brand
                        <input value={product.brand} type="text" name='brand' placeholder="Brand" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Color
                        <input value={product.color} type="text" name='color' placeholder="Color" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Size
                        <input value={product.size} type="text" size='size' placeholder="Size" className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Description
                        <textarea value={product.description} name="description" id="decription" cols="30" rows="10" className="full radius pad-inline glass height white-clr" placeholder="Description"></textarea>
                    </div>
                    <div className="items flex flex-column gap">
                        Quantity
                        <input value={product.category} type="text" placeholder="Quantity" name='quantity' className="full radius pad-inline glass height" />
                    </div>
                    <div className="items flex flex-column gap">
                        Weight
                        <input value={product.weight} type="text" placeholder="Weight" name='weight' className="full radius pad-inline glass" />
                    </div>
                    <div className="items flex flex-column gap">
                        Category
                        <div className="custom-datalist radius height">
                            <input value={product.category} type="text" placeholder="Category" name='category' className="full radius pad-inline glass" />
                        </div>
                    </div>
                </div>
                <input type="submit" className="radius red-bg white-clr flex a-center j-center height font-sm pointer ease hover-green" value='Update'/>
            </form>
        </div>
    )
}

export default Update
