// account.js
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    isLoggedIn: false,
    showNavbar: true,
    showSign: false,
    showLogin: true,
};

const accountSlice = createSlice({
    name: "account",
    initialState,
    reducers: {
        showAccount: (state) => {
            state.isLoggedIn = true;
            state.showNavbar = false;
        },
        hideAccount: (state) => {
            state.isLoggedIn = false;
            state.showNavbar = true;
        },
        resetNavbar: (state) => {
            state.showNavbar = true;
        },
        showSign: (state) => {
            state.showSign = true;
            state.showLogin = false;
        },
        showLogin: (state) => {
            state.showLogin = true;
            state.showLogin = false;
        },
    },
});

export const { showAccount, hideAccount, resetNavbar, loginOrSign } = accountSlice.actions;

export default accountSlice.reducer;
