import React, { useEffect, useState } from 'react'
import './Products.css'
import '../../cssHelper/helper.css'

function Products() {

    const [products, setProducts] = useState([])

    const fetchProducts = async () => {
        await fetch('http://localhost:4000/get-products')
            .then((result) => result.json())
            .then((data) => setProducts(data))
    }
    useEffect(() => {
        fetchProducts()
    }, [])


    return (
        <div className='Products-wrapper scroll-hidden black-clr w-full'>
            <div className="products scroll-y scroll-hidden-x grid c-repeat-5 p-3 gap-2">
                <div className="div">
                {
                    products.map((product, index) => (
                        <div className="products-list flex flex-col gap-1 pointer ease-4">
                            <img src={product.image ? product.image: ''} className='cover w-full product-image' />
                            <div className="products-details flex flex-col">
                                <p className="product-name font-sm bold">
                                   {product.name}
                                </p>
                                <p className='price font-md bold'>
                                    $<span>{product.price}</span>
                                </p>
                                <p className="description font-xs">
                                {product.description}
                                </p>
                                <p className="category mt-1">
                                    <span className='category-title'>Category</span> {product.category}
                                </p>
                                <p className="offer">
                                    Flaship sale is now
                                </p>
                            </div>
                        </div>
                    ))
                }
                </div>
            </div>
        </div>
    )
}

export default Products
