import { createSlice } from "@reduxjs/toolkit";

const initialState = false;

const accountSlice = createSlice({
    name: "account",
    initialState,
    reducers: {
        showAccount: (state) => true
    }
})

export const { showAccount } = accountSlice.actions;

export default accountSlice.reducer;
