import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css';
import './Cart.css'
import { useDispatch } from 'react-redux';

function Cart() {
    const dispatch = useDispatch();
    const [cart, setCart] = useState([])

    const getCart = async () => {
        await fetch('http://localhost:4000/getcart', {
            method: 'get',
            headers: {
                Accept: "application/json",
                'Content-Type': 'application/json', // Add this line
                'auth-token': `${localStorage.getItem('auth-token')}`,
            }
        })
            .then((result) => result.json())
            .then((data) => setCart(data))
    }
    useEffect(() => {
        getCart()
        console.log(cart);
    }, [])

    const removeFromCart = () => {
        // console.log(itemId);
        if (localStorage.getItem('auth-token')) {
            fetch('http://localhost:4000/removefromcart', {
                method: 'POST',
                headers: {
                    Accept: "application/json",
                    'Content-Type': 'application/json', // Add this line
                    'auth-token': `${localStorage.getItem('auth-token')}`,
                },
                body: JSON.stringify({ itemId: 3 })
            })
                .then((response) => response.json())
                .then((data) => console.log(data))
                .catch(err => console.log(err))
        }
    }



    return (
        <div className='Cart-wrapper w-full scroll-hidden'>
            <div className="cart scroll-y scroll-hidden-x grid c-repeat-2 w-full gap-1 p-inline-4 p-block-3">
                {
                    cart.length > 0 
                }
            </div>
        </div>
    )
}

export default Cart
