import React, { useState } from 'react'
import '../Account/Account.css'
import '../../cssHelper/helper.css'
import { useDispatch } from 'react-redux';
import { loginOrSign } from '../../redux/account';
import { showSign } from '../../redux/account'
import { useNavigate } from 'react-router-dom';

function Login() {

    // validation js config 
    inputElement.addEventListener('input', liveValidationSign);
    passwordSign.addEventListener('input', liveValidationSign);

    function liveValidationSign() {
        if (this.id === 'emailOrPhone') {
            validateEmail(this.value);
        } else if (this.id === 'password') {
            validatePassword(this.value);
        }
    }

    var validEmail = false;
    function validateEmail(value) {
        var errorDiv = document.querySelector('.email-error');
        var requiredError = errorDiv.querySelector('.email-required');
        var formatError = errorDiv.querySelector('.email-format');
        var serverErr = document.getElementById('server-error-message')

        var inputElement = document.getElementById('emailOrPhone');
        var nameAttribute = inputElement.getAttribute('name');
        console.log(nameAttribute);

        requiredError.style.display = formatError.style.display = serverErr.style.display = 'none';

        if (value === "") {
            validEmail = false;
            document.getElementById('emailOrPhone').style.border = "1px solid var(--accent-red)"
            requiredError.style.display = 'flex';
        } else if (!emailOrPhone.test(value)) {
            validEmail = false;

            document.getElementById('emailOrPhone').style.border = "1px solid var(--accent-red)"
            formatError.style.display = 'flex';

            // checking inputing is phone number or email 
            if (nameAttribute === 'phoneNumber') {
                formatError.innerHTML = 'Enter valid phone Number';
            } else {
                formatError.innerHTML = 'Enter valid email address';
            }
        } else {
            validEmail = true;
            document.getElementById('emailOrPhone').style.border = "1px solid var(--accent-green)"
        }
        checkValidity();
    }

    var validPassword = false;
    function validatePassword(value) {
        var errorDiv = document.querySelector('.password-error');
        var requiredError = errorDiv.querySelector('.password-required');
        var minLengthError = errorDiv.querySelector('.password-min-length');
        var serverErr = document.getElementById('server-error-message');

        requiredError.style.display = minLengthError.style.display = serverErr.style.display = 'none';

        if (value === "") {
            validPassword = false;
            document.getElementById('password').style.border = "1px solid var(--accent-red)"
            requiredError.style.display = 'flex';
        } else if (value.length < 8) {
            validPassword = false;
            document.getElementById('password').style.border = "1px solid var(--accent-red)"
            minLengthError.style.display = 'flex';
        } else {
            validPassword = true;
            document.getElementById('password').style.border = "1px solid var(--accent-green)"
        }
        checkValidity();
    }

    function validationSign() {
        return true;
    }

    function checkValidity() {
        if (validEmail && validPassword) {
            document.getElementById('submit-btn').disabled = false;
            console.log("enabled");
        } else {
            console.log("disabled");
            document.getElementById('submit-btn').disabled = true;
        }
    }
    //sign in validation ends
    // validation js ends 

    const navigate = useNavigate();

    const [loginData, setLoginData] = useState({
        email: '',
        password: ''
    });

    const addLogin = (e) => {
        const { name, value } = e.target;
        setLoginData({
            ...loginData,
            [name]: value
        });
    };

    const loginIn = async () => {
        let responseData;
        try {
            const response = await fetch('http://localhost:4000/login', {
                method: 'POST',
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(loginData),
            });

            responseData = await response.json();
            console.log(responseData);

            if (responseData.success) {
                localStorage.setItem('auth-token', responseData.token);
                window.location.replace('/');
            } else {
                console.log(responseData.error);
            }
        } catch (err) {
            console.error('Error:', err);
        }
    };

    const dispatch = useDispatch();

    return (
        <div className="login-wrapper account-wrapper radius flex flex-column a-center j-center gap pad shadow black-clr white-bg w-full">
            <p className="font-md bold">Login</p>
            <div className="form flex w-full flex-column relative">
                <label htmlFor="email">Enter your email or phone</label>
                <input onChange={addLogin} value={loginData.email} name='email' type="text" placeholder="Enter email address" className="radius" id="email" />
                <div className="email-error absolute right flex gap-1 a-center">
                    <h5 className="email-required light red-clr" style={{ display: 'none' }}>This field is required</h5>
                    <h5 className="email-format light red-clr" style={{ display: 'none' }}></h5>
                </div>

                <label htmlFor="password">Enter your password</label>
                <input onChange={addLogin} value={loginData.password} type="password" placeholder="Enter password" className="radius" name="password" id="password" />
                <div className="password-error absolute right flex gap-1 a-center">
                    <h5 className="light red-clr" id="server-error-message"></h5>
                    <h5 className="password-required light red-clr" style={{ display: 'none' }}>This field is required</h5>
                    <h5 className="password-min-length light red-clr" style={{ display: 'none' }}>Minimum 8 characters required</h5>
                </div>

                <p className="font-sm text-center">
                    OR
                </p>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="48" height="48" src="https://img.icons8.com/color/48/google-logo.png" alt="Google Logo" />
                    Continue with Google
                </div>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="48" height="48" src="https://img.icons8.com/color/48/microsoft.png" alt="Microsoft Logo" />
                    Continue with Microsoft account
                </div>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="50" height="50" src="https://img.icons8.com/ios-filled/50/mac-os.png" alt="Apple Logo" />
                    Continue with Apple ID
                </div>
                <p>
                    By continuing, you agree to Depot's <span className="terms pointer">Terms of Use</span> and <span className="terms pointer">Privacy Policy</span>
                </p>
                <input onClick={loginIn} type="submit" className="radius submit font-sm bold white-clr pointer" value="Get OTP" />
            </div>
            <p className="text-center">
                Don't have an account? <span className='pointer' onClick={() => navigate('/account/sign')}>Signup</span>
            </p>
        </div>
    )
}

export default Login
