// account.js
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isLoggedIn: false,
  showNavbar: true,
  showSign: true,
};

const accountSlice = createSlice({
  name: "account",
  initialState,
  reducers: {
    showAccount: (state) => {
      state.isLoggedIn = true;
      state.showNavbar = false;
    },
    hideAccount: (state) => {
      state.isLoggedIn = false;
      state.showNavbar = true;
    },
    resetNavbar: (state) => {
      state.showNavbar = true;
    },
    loginOrSign: (state) => {
        state.showSign = true;
      },
  },
});

export const { showAccount, hideAccount, resetNavbar } = accountSlice.actions;

export default accountSlice.reducer;
