import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { loginOrSign } from '../../redux/account';
import { showSign } from '../../redux/account';
import '../Account/Account.css';
import '../../cssHelper/helper.css';

function Login() {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    // State for login data and error messages
    const [loginData, setLoginData] = useState({
        email: '',
        password: ''
    });

    const [emailError, setEmailError] = useState({
        required: false,
        format: false
    });

    const [passwordError, setPasswordError] = useState({
        required: false,
        minLength: false
    });

    // Function to handle input changes
    const addLogin = (e) => {
        const { name, value } = e.target;
        setLoginData({
            ...loginData,
            [name]: value
        });

        // Reset corresponding error when user starts typing again
        if (name === 'email') {
            setEmailError({
                required: false,
                format: false
            });
        } else if (name === 'password') {
            setPasswordError({
                required: false,
                minLength: false
            });
        }
    };

    // Function to perform login
    const loginIn = async () => {
        // Perform validation
        let isValid = true;

        if (loginData.email.trim() === '') {
            setEmailError({
                ...emailError,
                required: true
            });
            isValid = false;
        }

        if (!/\S+@\S+\.\S+/.test(loginData.email)) {
            setEmailError({
                ...emailError,
                format: true
            });
            isValid = false;
        }

        if (loginData.password.length < 8) {
            setPasswordError({
                ...passwordError,
                minLength: true
            });
            isValid = false;
        }

        // If all validations pass, proceed with login
        if (isValid) {
            try {
                const response = await fetch('http://localhost:4000/login', {
                    method: 'POST',
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(loginData)
                });

                const responseData = await response.json();
                console.log(responseData);

                if (responseData.success) {
                    localStorage.setItem('auth-token', responseData.token);
                    window.location.replace('/');
                } else {
                    console.log(responseData.error);
                }
            } catch (err) {
                console.error('Error:', err);
            }
        }
    };

    return (
        <div className="login-wrapper account-wrapper radius flex flex-column a-center j-center gap pad shadow black-clr white-bg w-full">
            <p className="font-md bold">Login</p>
            <div className="form flex w-full flex-column relative">
                <label htmlFor="email">Enter your email or phone</label>
                <input
                    onChange={addLogin}
                    value={loginData.email}
                    name="email"
                    type="text"
                    placeholder="Enter email address"
                    className="radius"
                    id="email"
                />
                <div className="email-error absolute right flex gap-1 a-center">
                    <h5 className={`email-required light red-clr ${emailError.required ? '' : 'hidden'}`}>
                        This field is required
                    </h5>
                    <h5 className={`email-format light red-clr ${emailError.format ? '' : 'hidden'}`}>
                        Invalid email format
                    </h5>
                </div>

                <label htmlFor="password">Enter your password</label>
                <input
                    onChange={addLogin}
                    value={loginData.password}
                    type="password"
                    placeholder="Enter password"
                    className="radius"
                    name="password"
                    id="password"
                />
                <div className="password-error absolute right flex gap-1 a-center">
                    <h5 className={`password-required light red-clr ${passwordError.required ? '' : 'hidden'}`}>
                        This field is required
                    </h5>
                    <h5 className={`password-min-length light red-clr ${passwordError.minLength ? '' : 'hidden'}`}>
                        Minimum 8 characters required
                    </h5>
                </div>

                <p className="font-sm text-center">OR</p>
                {/* Google, Microsoft, Apple login buttons */}
                {/* ... */}

                <p>
                    By continuing, you agree to Depot's <span className="terms pointer">Terms of Use</span> and{' '}
                    <span className="terms pointer">Privacy Policy</span>
                </p>
                <input onClick={loginIn} type="submit" className="radius submit font-sm bold white-clr pointer" value="Get OTP" />
            </div>
            <p className="text-center">
                Don't have an account? <span className="pointer" onClick={() => navigate('/account/sign')}>Signup</span>
            </p>
        </div>
    );
}

export default Login;
