import React, { useState } from 'react';
import '../Account/Account.css';
import '../../cssHelper/helper.css';
import { useDispatch } from 'react-redux';
import { loginOrSign } from '../../redux/account';
import { useNavigate } from 'react-router-dom';

function Login() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    
    const [loginData, setLoginData] = useState({
        email: '',
        password: ''
    });

    const [validEmail, setValidEmail] = useState(true);
    const [validPassword, setValidPassword] = useState(true);

    const addLogin = (e) => {
        const { name, value } = e.target;
        setLoginData({
            ...loginData,
            [name]: value
        });
    };

    const validateEmail = () => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const isValid = emailRegex.test(loginData.email);
        setValidEmail(isValid);
        return isValid;
    };

    const validatePassword = () => {
        const isValid = loginData.password.length >= 8;
        setValidPassword(isValid);
        return isValid;
    };

    const loginIn = async () => {
        if (!validateEmail() || !validatePassword()) {
            return; // Do not proceed if validation fails
        }

        try {
            const response = await fetch('http://localhost:4000/login', {
                method: 'POST',
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(loginData),
            });

            const responseData = await response.json();
            console.log(responseData);

            if (responseData.success) {
                window.location.replace('/');
            } else {
                // Handle login failure
                console.log(responseData.error);
            }
        } catch (err) {
            console.error('Error:', err);
        }
    };

    return (
        <div className="login-wrapper account-wrapper radius flex flex-column a-center j-center gap pad shadow black-clr white-bg w-full">
            <p className="font-md bold">Login</p>
            <div className="form flex w-full flex-column relative">
                <label htmlFor="email">Enter your email or phone</label>
                <input
                    onChange={addLogin}
                    value={loginData.email}
                    name='email'
                    type="text"
                    placeholder="Enter email address"
                    className={`radius ${validEmail ? '' : 'invalid'}`}
                    id="email"
                    onBlur={validateEmail}
                />
                {!validEmail && <p className="error-message">Please enter a valid email address</p>}

                <label htmlFor="password">Enter your password</label>
                <input
                    onChange={addLogin}
                    value={loginData.password}
                    type="password"
                    placeholder="Enter password"
                    className={`radius ${validPassword ? '' : 'invalid'}`}
                    name="password"
                    id="password"
                    onBlur={validatePassword}
                />
                {!validPassword && <p className="error-message">Password must be at least 8 characters long</p>}

                <p className="font-sm text-center">
                    OR
                </p>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="48" height="48" src="https://img.icons8.com/color/48/google-logo.png" alt="Google Logo" />
                    Continue with Google
                </div>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="48" height="48" src="https://img.icons8.com/color/48/microsoft.png" alt="Microsoft Logo" />
                    Continue with Microsoft account
                </div>
                <div className="google w-full radius border-green height flex a-center gap pad-inline pointer">
                    <img width="50" height="50" src="https://img.icons8.com/ios-filled/50/mac-os.png" alt="Apple Logo" />
                    Continue with Apple ID
                </div>
                <p>
                    By continuing, you agree to Depot's <span className="terms pointer">Terms of Use</span> and <span className="terms pointer">Privacy Policy</span>
                </p>
                <input
                    onClick={loginIn}
                    type="submit"
                    className="radius submit font-sm bold white-clr pointer"
                    value="Get OTP"
                    id="submit-btn"
                    disabled={!validEmail || !validPassword}
                />
            </div>
            <p className="text-center">
                Don't have an account? <span className='pointer' onClick={() => navigate('/account/sign')}>Signup</span>
            </p>
        </div>
    );
}

export default Login;
